package com.cn.weixuan.util;import javax.servlet.http.HttpServletRequest;import com.cn.weixuan.base.module.CacheUser;import com.cn.weixuan.pojo.User;import org.apache.shiro.SecurityUtils;/** * @author * @description * @date 2020/4/30 */public class UserUtils {    public static final String USER_TOKEN_KEY = "USER_TOKEN_KEY_FTYBA12F31SDF";    /**     * 判断是否登录成功     */    public static boolean isLogin(HttpServletRequest request) {        Object obj = request.getSession().getAttribute(USER_TOKEN_KEY);        if (obj != null) {            return true;        }        return false;    }    /**     * 登录成功设置到session里     */    public static void setLoginSuccess(HttpServletRequest request, User user) {        request.getSession().setAttribute(USER_TOKEN_KEY, user);    }    public static User getUser() {        User user = (User) SecurityUtils.getSubject().getPrincipal();        return user;    }}